INSERT INTO `main_items` (`id`, `img`, `icon`, `name`, `text`, `identifier`, `url`) VALUES
(1, '1.jpg', NULL, 'ФИКСИРОВАННАЯ СТАВКА ТАКСОПАРКА MY WAY НА СУТКИ ВМЕСТО ПРОЦЕНТОВ, КАК ВАМ?', 'НЕ ХОТИТЕ ПЛАТИТЬ % ТАКСОПАРКУ С КАЖДОЙ ПОЕЗДКИ? НЕ ВОПРОС, ВЫБИРАЙТЕ ФИКСИРОВАННУЮ СТАВКУ И РАБОТАЙТЕ НА СЕБЯ!\r\nМИНИМАЛЬНАЯ СТАВКА КОМИССИИ ТАКСОПАРКА – 3%\r\n', 1, NULL),
(2, 'slide-2.jpg', NULL, 'ПРИГЛАШАЕМ ВОДИТЕЛЕЙ ИЗ ДРУГИХ ТАКСОПАРКОВ!', 'ВАМ НЕ НУЖНО УХОДИТЬ ЕСЛИ ВАС ВСЕ УСТРАИВАЕТ. РЕГИСТРИРУЙТЕСЬ И ПОЛУЧАЙТЕ ДОСТУП К НАШИМ СЕРВИСАМ.\r\n*ПЛАТНО ДЛЯ ВОДИТЕЛЕЙ НЕ ЗАРЕГИСТРИРОВАННЫХ В ТАКСОПАРКЕ MY WAY\r\n', 1, NULL),
(3, '3.jpg', NULL, 'НОВЫЙ СЕРВИС ДЛЯ ВОДИТЕЛЕЙ ЯНДЕКС ТАКСИ И ПАССАЖИРОВ (В РАЗРАБОТКЕ)', 'МОИ ПОСТОЯННЫЕ КЛИЕНТЫ И ЕГО АНАЛОГ ДЛЯ ПАССАЖИРОВ МОИ ПОСТОЯННЫЕ ВОДИТЕЛИ УКРЕПИТ ВАШУ СВЯЗЬ С ПРОВЕРЕННЫМИ ПАРТНЕРАМИ И ОБЛЕГЧИТ ПЛАНИРОВАНИЕ ПОЕЗДОК', 1, NULL),
(4, '6.jpg', NULL, 'СПРАВОЧНИК КАЛЕНДАРЬ ГОРОДА С ТОЧКАМИ ПОВЫШЕННОГО СПРОСА (В РАЗРАБОТКЕ)', 'ВЫБЕРИТЕ САМЫЕ ПРИВЛЕКАТЕЛЬНЫЕ И УДОБНЫЕ ДЛЯ ВАС ТОЧКИ ПОВЫШЕННОГО СПРОСА, СФОРМИРУЙТЕ СОБСТВЕННЫЙ МАРШРУТ И ЗАРАБАТЫВАЙТЕ! ', 1, NULL),
(5, '5.jpg', NULL, 'ПОМОГАЕМ НОВИЧКАМ ОСВОИТЬСЯ В СЕРВИСЕ ЯНДЕКС ТАКСИ', 'НЕ ЗНАЕТЕ С ЧЕГО НАЧАТЬ? НИЗКИЙ РЕЙТИНГ В ЯНДЕКС ТАКСИ? КАК НЕ СОВЕРШИТЬ ОШИБКИ В РАБОТЕ? МЫ РАССКАЖЕМ ВСЕ ЧТО МОЖНО ДЕЛАТЬ И ЧЕГО ДЕЛАТЬ НЕ СТОИТ ВОДИТЕЛЮ ДЛЯ ПРОФЕССИОНАЛЬНОГО РОСТА В ЯНДЕКС ТАКСИ', 1, NULL),
(7, NULL, 'User', 'Новинка! Сервис мои постоянные клиенты', 'Не секрет что у большинства из нас водителей Яндекс такси есть свои постоянные клиенты которых мы бережем и собираем из числа пассажиров.\r\nДля удобства управления заказами, дайте своим клиентам адрес сайта My Way и свой номер мобильного телефона для ссылки при регистрации и все ваши постоянные клиенты смогут видеть ваш календарь загрузки, какие даты свободны, какое время свободно. Стоимость вашего часа. И многое другое. Естественно включая бронирование и оплату. Вы сможете и самостоятельно добавлять своих постоянных клиентов, редактировать и удалять. Это ваш рабочий кабинет, который видите лишь вы и не видят другие водители или пассажиры.', 2, 'my_regular_passengers'),
(8, NULL, 'Column', 'Новинка! Сервис районы повышенного спроса в вашем городе', 'В Яндекс такси сейчас существует карта с зонами повышенного спроса в которых как считается “мало” водителей и “много” запросов от пассажиров. Но что делать когда пиковый спрос уходит? И тут для вас мы создали календарь справочник города с точками спроса. Сейчас сервис в разработке и наполнении. Будет доступен всем водителям присоединившимся к таксопарку My Way', 2, NULL),
(9, NULL, 'Pencil', 'Отзывы о водителях и пассажирах, смело пишите не опасаясь ничего', 'Не модерируемое пространство где вы сможете опубликовать свои слова о пассажире или водителе, прикрепить фото к комментарию. Открыть для общего доступа или настроить видимость публикации только для ограниченного круга людей.\r\nУ всех зарегистрированных пользователей имеющих доступ будет возможность прокомментировать увиденное прикрепить фото или видео а также оценить публикацию привычными звездами\r\n', 2, NULL),
(10, NULL, 'Users', 'Автоматическое получение заказов от находящихся поблизости пассажиров', 'Если вы впервые в Яндекс такси, вам стоит знать что алгоритм распределения заказов достаточно прост. Заказ пассажира первыми увидят те, кто обладает высоким рейтингом и активностью  и при этом близок к пассажиру. Дальше по ниспадающей, чем ниже активность и рейтинг - тем дальше в очередности получения заказа.\r\nНо! На этот процесс не влияет диспетчер. Ничего личного. Только алгоритм. Поэтому постоянно развивая свой рейтинг и повышая активность вы сможете быть уверенным в справедливом распределении заказов пассажиров Яндекс такси.\r\n', 2, NULL),
(11, NULL, 'StorageBox', 'Различные программы лояльности для водителей, в том числе бонусы и скидки', 'Если вы недавно в Яндекс такси и еще не полностью освоились, знайте - в Яндекс такси существует и отлично работает система поощрения в виде бонусов для водителей и специальных скидочных купонов для них. Аналогично и для пассажиров существует система купонов для особых случаев.\r\nМы также работаем над программой лояльности и в ближайшее время по окончании ее разработки опубликуем на сайте.\r\n', 2, NULL),
(12, NULL, 'Share', 'Расчет кратчайшего пути до клиента учитывая дорожные пробки', 'В работе вторым после таксометра основным вашим инструментом будет Яндекс навигатор. Маршрут до пассажира а также маршрут поездки пассажира будет построен программно по самому короткому пути с минимальным пробегом.\r\nОтдельно мы расскажем вам как читать навигатор, как заранее перестраиваться в потоках, следить внимательно за знаками ограничения скорости, учитывать маршруты с платными дорогами и перекрытия шлагбаумами и пр.\r\nНужно будет сохранять выдержку, быть максимально спокойным и уравновешенным, не реагировать на дорожные провокации и поездка понравится вам и пассажиру который будучи довольным отметит ее звездами для вашего рейтинга.\r\n', 2, NULL),
(13, NULL, 'Key', 'Бесплатное подключение', 'Это так и есть, вам достаточно полностью и аккуратно заполнить анкету, закачать необходимые сканы водительского удостоверения с обеих сторон, все страницы паспорта, обе стороны СТС и дождаться времени проверки оператором (не более двух часов).\r\nПосле чего вы получите sms с инструкцией что сделать поэтапно для выхода на линию и начать зарабатывать.\r\n', 2, NULL),
(14, NULL, 'Time', 'Отсутствие ограничений в графике времени работы', 'Это буквально так и есть, с небольшой оговоркой. Вы сможете работать в течение суток не более 16 часов. Такое ограничение включено в программном обеспечении таксометра Яндекс такси.\r\nАвтор этих строк на сайте имеет реальный опыт работы по 16 часов в сутки. Не думаю что вам понадобится когда либо работать так долго. При грамотном построении стратегии ваш рабочий день будет от 8 до 10 часов. Хотя если решите что в день вам достаточно и трех тысяч дохода, не вопрос, работайте меньше.\r\n', 2, NULL),
(15, NULL, 'Mastercard', 'Прозрачная система оплаты труда (нет скрытых комиссий и переплат)', 'Это практически именно так, за редким исключением о котором вам стоит знать. Для примера: если ваш пассажир ехал с вами по безналу и по окончании поездки программа Яндекс такси не смогла списать с его карты деньги за поездку, оплата зависнет на несколько дней, о чем вы будете получать уведомления. При этом Яндекс такси спишет с вас комиссии за поездку еще до того, как поступят деньги от пассажира по безналу. Так работает система и независимо от того, согласен с ней водитель или не согласен - она работает именно так. Сообщаем об этом открыто а не молчим.\r\n', 2, NULL),
(16, NULL, 'Car', 'Помогаем в оформлении лицензии на автомобиль и не только', 'Да, мы за легальный бизнес! За лицензирование. За оформление и брендирование автомобиля. За работу водителя в качестве ИП а не физлица.\r\nДля этого мы развиваем юридическую поддержку и всем, кто решит поддержать наше стремление в легальности бизнеса окажем посильную поддержку в оформлении лицензии, ИП и брендировании автомобиля.\r\nВсе это в комплексе дает вам больше прав на дороге, где вы будете проводить достаточно долгое время своей жизни. Так согласитесь что лучше работать легально чем вздрагивать когда вас останавливает сотрудник ДПС.\r\n', 2, NULL),
(17, NULL, 'ChartUp', 'Высокий рейтинг компании обеспечивающий большое количество заказов', 'Наверно стоит сказать прямо что рейтинг компании складывается из рейтинга водителей работающих в таксопарке. Поэтому мы будем максимально поддерживать тех, кто учитывает наши рекомендации и постоянно работает над ростом рейтинга и качества оказанных услуг.\r\nК чему это делать? Да к тому, что поднимая свой рейтинг вы получаете заказ в числе первых, вы тот кто имеет поддержку в яндекс таксометре и платные купоны покрывающие инциденты с пассажирами. И конечно как не сказать что поднимая качество своей работы вы услышите от пассажира вопрос: а нет ли у вас визитной карточки, я хочу позвонить вам для постоянных поездок но лично с вами. Как это использовать мы уже писали в разделе Сервис мои постоянные клиенты.\r\nВот так поднимая качество своей работы вы уходите постепенно от необходимости постоянной работы на линии в Яндекс такси уделяя больше внимания своим персональным пассажирам.', 2, NULL),
